dict.string:
cat | perl -e '

# read first line to get the column names (header)
$firstLine = <>;

# bail if nothing could read
if(!defined($firstLine)) {
	exit 0;
}

# store the column names
chomp($firstLine);
$firstLine =~ s/\"/\\\"/g;  # escape "
@header = split(/\t/, $firstLine);

$h_cnt = $#header;     # number of columns

# get the column definitions
open(META, $ENV{"SP_BUNDLE_INPUT_TABLE_METADATA"}) or die $!;
@meta = ();
while(<META>) {
	chomp();
	my @arr = split(/\t/);
	push @meta, \@arr;
}
close(META);

print "||";
for($i=0; $i<=$h_cnt; $i++) {
	print " *$header[$i]* ||";
}
print "\n";

# read row data of each selected row
$rowData=<>;
while($rowData) {

	print "||";

	# remove line ending
	chomp($rowData);

	# escape "
	$rowData=~s/\"/\\\"/g;

	# split column data which are tab-delimited
	@data = split(/\t/, $rowData);
	for($i=0; $i<=$h_cnt; $i++) {
		# re-escape \t and \n
		$cellData = $data[$i];
		$cellData =~ s/↵/\n/g;
		$cellData =~ s/⇥/\t/g;

		# check for data types
		if($cellData eq "NULL") {
			print " _NULL_ ";
		} else {
			chomp($cellData);
			print " *$cellData* ";
		}
		print "||";
	}

	print "\n";

	# get next row
	$rowData=<>;
}

print "\n";
' | __CF_USER_TEXT_ENCODING=$UID:0x8000100:0x8000100 pbcopy
