dict.string:
#!/usr/bin/perl

use strict;
use warnings;

local $/;
my $sql = <STDIN>;
$sql = '' unless defined $sql;
print $sql and exit 0 unless $sql =~ /\S/;

my @placeholders;
my $placeholder_prefix;
do {
    $placeholder_prefix = "__SA_FMT_" . $$ . "_" . int(rand(1_000_000_000)) . "__";
} while (index($sql, $placeholder_prefix) >= 0);

my $line_comment_break = "${placeholder_prefix}LINE_BREAK__";
sub _stash_placeholder {
    my ($value) = @_;
    my $token = "${placeholder_prefix}" . scalar(@placeholders) . "__";
    push @placeholders, $value;
    return $token;
}

# Temporarily replace quoted strings and comments so formatter regexes do not alter their contents.
my $working = $sql;
$working =~ s{
    (`(?:``|[^`])*`)
  | ('(?:\\.|''|[^'])*')
  | ("(?:\\.|""|[^"])*")
  | (/\*.*?\*/)
  | (--[^\n]*(?:\n|$))
  | (\#[^\n]*(?:\n|$))
}{
    my $matched = $&;
    if (defined $5 || defined $6) {
        my $had_newline = ($matched =~ s/\n\z//);
        my $token = _stash_placeholder($matched);
        $token .= $line_comment_break if $had_newline;
        $token;
    } else {
        _stash_placeholder($matched);
    }
}gsex;

$working =~ s/\r\n?/\n/g;
$working =~ s/[ \t]+/ /g;
$working =~ s/\s*;\s*/;\n/g;
$working =~ s/\n+/ /g;
$working =~ s/^\s+|\s+$//g;

my @keywords = (
    'LEFT OUTER JOIN', 'RIGHT OUTER JOIN', 'ALTER TABLE', 'CREATE TABLE',
    'CREATE INDEX', 'DELETE FROM', 'INSERT INTO', 'UNION ALL', 'ORDER BY',
    'GROUP BY', 'LEFT JOIN', 'RIGHT JOIN', 'INNER JOIN', 'OUTER JOIN',
    'ON UPDATE', 'ON DELETE', 'SELECT', 'UPDATE', 'VALUES', 'HAVING',
    'WHERE', 'LIMIT', 'FROM', 'JOIN', 'UNION', 'EXCEPT', 'INTERSECT',
    'SET', 'AND', 'OR', 'XOR', 'ON', 'ADD', 'AFTER', 'DROP'
);

my @keyword_patterns = map {
    my $pattern = quotemeta($_);
    $pattern =~ s/\\ /\\s+/g;
    $pattern;
} sort { length($b) <=> length($a) } @keywords;

my $keyword_pattern = join '|', @keyword_patterns;
$working =~ s/\s*\b($keyword_pattern)\b\s*/"\n" . uc($1) . " "/ige;

$working =~ s/\s+,/,/g;
$working =~ s/,\s+/, /g;
$working =~ s/\(\s+/(/g;
$working =~ s/\s+\)/)/g;
$working =~ s/[ ]+\n/\n/g;
$working =~ s/\n{2,}/\n/g;
$working =~ s/^\n+//;
$working =~ s/\s+$//;

my @raw_lines = split /\n/, $working;
my @lines = grep { length } map {
    my $line = $_;
    $line =~ s/^\s+|\s+$//g;
    $line;
} @raw_lines;

my $indent = 0;
my @formatted_lines;
for my $line (@lines) {
    if ($line =~ /^\)/) {
        $indent-- if $indent > 0;
    }

    push @formatted_lines, ('  ' x $indent) . $line;

    my $opens = () = $line =~ /\(/g;
    my $closes = () = $line =~ /\)/g;
    $indent += ($opens - $closes);
    $indent = 0 if $indent < 0;
}

my $formatted = join("\n", @formatted_lines);

for my $idx (0 .. $#placeholders) {
    my $token = "${placeholder_prefix}${idx}__";
    my $value = $placeholders[$idx];
    $formatted =~ s/\Q$token\E/$value/g;
}
$formatted =~ s/\Q$line_comment_break\E/\n/g;

print $formatted;
